<!DOCTYPE html><html lang="en"><head>  <title>Bootstrap Example</title>  <meta charset="utf-8">  <meta name="viewport" content="width=device-width, initial-scale=1">  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css">  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/js/bootstrap.min.js"></script></head><style>	@import url('https://fonts.googleapis.com/css?family=Nunito');@import url('https://fonts.googleapis.com/css?family=Poiret+One');body, html {	height: 100%;	background-repeat: no-repeat;    /*background-image: linear-gradient(rgb(12, 97, 33),rgb(104, 145, 162));*/	background:#1e797e;	position: relative;}#particles-js{  	width: 100%;  	height: 100%;  	background-size: cover;  	background-position: 50% 50%;  	position: fixed;  	top: 0px;  	z-index:1;}.logo_back{	width: 0;	height: 0;	border-top:200px solid #fff;	border-right: 400px solid transparent;	}	.img_cir{			width: 200px;	height: 200px;	-moz-border-radius: 50px;	-webkit-border-radius: 50px;	border-radius: 50%;	}	.bac_img{	    background-image: url("img/bac_img.png");    height: 100%;     background-position: center;    background-repeat: no-repeat;    background-size: cover;		} .blink_me {  animation: blinker 5s linear infinite; } @keyframes blinker {     50% { opacity: 0; } }</style><body class="bac_img "><div id="particles-js"></div>	<div class="logo_back" style="position:relative;">			</div>		<img class="img-responsive" style="position:absolute;top:12px;left:66px" src="img/logo.png">	<!--<img  class="img_cir" style="position:absolute;top:-20px;left:50px" src="img/logo_full2.png">-->	<!--<div class="" style=" position:absolute;top:10%;left:20%;">		<img class="img-responsive" src="img/com_cartinhour.png">	</div>-->	<div style=" position:absolute;top:40%;right:-10px;">		<img class="img-responsive " style="border-radius:20px;" src="img/com_soon3.png">	</div></body><script>	$.getScript("https://cdnjs.cloudflare.com/ajax/libs/particles.js/2.0.0/particles.min.js", function(){    particlesJS('particles-js',      {        "particles": {          "number": {            "value": 80,            "density": {              "enable": true,              "value_area": 800            }          },          "color": {            "value": "#ffffff"          },          "shape": {            "type": "circle",            "stroke": {              "width": 0,              "color": "#000000"            },            "polygon": {              "nb_sides": 5            },            "image": {              "width": 100,              "height": 100            }          },          "opacity": {            "value": 0.5,            "random": false,            "anim": {              "enable": false,              "speed": 1,              "opacity_min": 0.1,              "sync": false            }          },          "size": {            "value": 5,            "random": true,            "anim": {              "enable": false,              "speed": 40,              "size_min": 0.1,              "sync": false            }          },          "line_linked": {            "enable": true,            "distance": 150,            "color": "#ffffff",            "opacity": 0.4,            "width": 1          },          "move": {            "enable": true,            "speed": 6,            "direction": "none",            "random": false,            "straight": false,            "out_mode": "out",            "attract": {              "enable": false,              "rotateX": 600,              "rotateY": 1200            }          }        },        "interactivity": {          "detect_on": "canvas",          "events": {            "onhover": {              "enable": true,              "mode": "repulse"            },            "onclick": {              "enable": true,              "mode": "push"            },            "resize": true          },          "modes": {            "grab": {              "distance": 400,              "line_linked": {                "opacity": 1              }            },            "bubble": {              "distance": 400,              "size": 40,              "duration": 2,              "opacity": 8,              "speed": 3            },            "repulse": {              "distance": 200            },            "push": {              "particles_nb": 4            },            "remove": {              "particles_nb": 2            }          }        },        "retina_detect": true,        "config_demo": {          "hide_card": false,          "background_color": "#b61924",          "background_image": "",          "background_position": "50% 50%",          "background_repeat": "no-repeat",          "background_size": "cover"        }      }    );});</script></html>